import React, { Component } from "react";
import { Link } from 'react-router-dom';
import { withRouter } from 'react-router-dom';
import { connect } from 'unistore/react';
import { actions } from '../store';

class MyCartPage extends Component {
    render() {
        // if ((!this.props.is_login) && (this.props.userStatus !== "pembeli")) {
        //     this.props.history.push('/')
        // }

        return (
            <div class="container my-5">
                <div class="row">
                    <div class='col-12 col-md-8'>
                        <div class="card">
                            <div class="container">
                                <div class="row">
                                    <aside class="col-3 border">
                                        <article class="gallery-wrap">
                                            <div class="img-big-wrap p-4">
                                                <img src="" width="100%" />
                                            </div>
                                        </article>
                                    </aside>
                                    <aside class="col-8">
                                        <article class="p-4 text-left">
                                            <p class="title mb-3">namaProduk}</p>
                                            <div class="mb-3">
                                                <var class="price h5 text-warning">
                                                    <span class="currency">Rp</span>
                                                    <span class="num"> harga}</span>
                                                    <sub class="currency">(Rp</sub>
                                                    <sub class="num"> harga)</sub>
                                                </var>
                                            </div>
                                            <dl class="row">
                                                <dt class="col-sm-4">Stock</dt>
                                                <dd class="col-sm-8">qty}</dd>
                                            </dl>
                                        </article>
                                    </aside>
                                    <div class='col-1'>
                                        <a href="#" class="btn btn-danger float-right my-auto"> <i class="fas fa-trash-alt"></i></a>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div class="col-md-4 col-12">
                        <dl class="row p-2 pt-3">

                            <dt class="col-sm-6">Total Payment</dt>
                            <dd class="col-sm-6">Rp </dd>

                        </dl>
                        <hr />

                    </div>
                </div>
            </div>
        );
    }
}

export default connect('is_login, userStatus', actions)
    (withRouter(MyCartPage));
