}

const baseUrl = 'localhost:8010/proxy'

export const store = createStore(initialState)

export const actions = store => ({
    setField: (state, event) => {
        return { [event.target.name]: event.target.value };
    },

    postLogout: state => {
        return {
            is_login: false,
            token: ""
        };
    },
    ChangeValue: (state, data, stats) => {
        if (stats == 1) {
            return { cities: data, MoodCityStat: stats };
        }
        if (stats == 2) {
            return { moods: data, MoodCityStat: stats };
        }
    },

    getLogin: async state => {
        // const data = { username: state.username, password: state.password };
        const url = "http://localhost:8010/proxy/login/pembeli"
        const headers = {
            method:'get',
            url: url,
            headers:{            
            'Content-Type': 'application/json'},
            data:{
                username: state.username, password: state.password
            }
          };
        await axios(headers)
            .then(function(response) {
                console.log("enek ta gak",response.data);
                if (response.data.status === 200) {
                    console.log("ikilo",response.token)
                    store.setState({
                        is_login: true,
                    });
                }
            })

    },

    postLoginPembeli: async (state) => {
        const data = {
            username: state.username,
            password: state.password,
        };
        await axios
            .get(baseUrl + '/login/pembeli', data, {
                headers: {
                    'Content-Type': 'application/json',
                }
            })
            .then(function (response) {
                console.log('response cuk', response)
                if (response.status == 200) {
                    store.setState({
                        is_login: true,
                        token: response.data.token,
                        userStatus: response.data.statusUser
                    })
                }
            })
            .catch(function (error) {
                console.log(error);
            });
    },

    postLoginPenjual: async state => {
        const data = {
            username: state.username,
            password: state.password,
        };
        await axios
            .get(baseUrl + 'login/penjual', data)
            .then(function (response) {
                if (response.data.hasOwnProperty('token')) {
                    store.setState({
                        is_login: true,
                        token: response.data.token,
                        userStatus: response.data.statusUser
                    })
                }
            })
            .catch(function (error) {
                console.log(error);
            });
    },

    GetPublic: async (state) => {
        await axios
            .get( baseUrl + 'public')
            .then(function (response) {
                store.setState({listRetro: response.data.products})
                console.log("thiiis",response.data);
                console.log(this.state.listRetro);
           })
            .catch(function(error){
                console.log(error);
            });
    },
    searchNews: async (state, keyword) => {
        if (keyword.length > 2) {
            try {
                {
                    store.getState().listSong.map((item, key) => {
                        var counter = 0;
                        if (_.includes(item.track.name.toLowerCase(), keyword.toLowerCase())) {
                            store.getState().dataSearch.push(item)
                        }
                    })
                }

                store.setState({ listSong: store.getState().dataSearch });
            }
            catch (error) {
                console.log(error);
            }
        }
        if (keyword.length <= 2) {
            try {
                const response = await axios.get(
                    "https://api.spotify.com/v1/playlists/" + store.getState().Thrower,
                    {
                        headers: {
                            "Authorization": "Bearer " + Bearer,
                        }
                    })
                // console.log(store.getState().listSong);
                store.setState({ listSong: response.data.tracks.items });
                store.getState().dataSearch = []
            }
            catch (error) {
                console.log(error);
            }
        }
    },
})
